from typing import List


def solve(loads: List[int]) -> int:

    dp = {}

    return recursiveDP(loads, 0, 0, 0, dp)


def recursiveDP(loads: List[int], currentIndex: int, server1: int, server2: int, dp: dict):

    if currentIndex == len(loads):
        return abs(server1-server2)
    
    key = (currentIndex, server1, server2)

    if key in dp:
        return dp[key]
    
    distributeToServer1 = recursiveDP(loads, currentIndex+1, server1+loads[currentIndex], server2, dp)
    distributeToServer2 = recursiveDP(loads, currentIndex+1, server1, server2+loads[currentIndex], dp)

    dp[key] = min(distributeToServer1, distributeToServer2)

    return dp[key]

